import React, { useState, useEffect, useContext, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { UserContext } from '../../context/UserContext';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import { FiChevronLeft, FiChevronRight, FiSettings, FiBookmark, FiShare2, FiHeart, FiStar } from 'react-icons/fi';
import { FaHeart, FaBookmark } from 'react-icons/fa';
import toast from 'react-hot-toast';

const ReaderContainer = styled.div`
  height: 100vh;
  background: ${props => props.theme.background};
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const ReaderHeader = styled.div`
  background: ${props => props.theme.card};
  border-bottom: 1px solid ${props => props.theme.border};
  padding: 1rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  z-index: 10;
`;

const BookInfo = styled.div`
  flex: 1;
`;

const BookTitle = styled.h1`
  font-size: 1.2rem;
  font-weight: 600;
  color: ${props => props.theme.text};
  margin: 0;
  margin-bottom: 0.25rem;
`;

const BookAuthor = styled.p`
  font-size: 0.9rem;
  color: ${props => props.theme.textLight};
  margin: 0;
`;

const ReaderActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled(motion.button)`
  width: 40px;
  height: 40px;
  border-radius: 8px;
  border: 1px solid ${props => props.theme.border};
  background: ${props => props.theme.backgroundSecondary};
  color: ${props => props.theme.text};
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.accent};
    transform: scale(1.05);
  }

  &:active {
    transform: scale(0.95);
  }
`;

const ReaderContent = styled.div`
  flex: 1;
  overflow: hidden;
  position: relative;
`;

const TextContent = styled.div`
  padding: 2rem;
  max-width: 800px;
  margin: 0 auto;
  font-size: ${props => props.fontSize || '1.1rem'};
  line-height: ${props => props.lineHeight || '1.6'};
  color: ${props => props.theme.text};
  text-align: justify;
  hyphens: auto;
  word-wrap: break-word;
  overflow-y: auto;
  height: 100%;

  p {
    margin-bottom: 1.5rem;
  }

  h1, h2, h3, h4, h5, h6 {
    margin-top: 2rem;
    margin-bottom: 1rem;
    color: ${props => props.theme.primary};
    font-weight: 600;
  }

  h1 { font-size: 2em; }
  h2 { font-size: 1.5em; }
  h3 { font-size: 1.2em; }
`;

const ReaderFooter = styled.div`
  background: ${props => props.theme.card};
  border-top: 1px solid ${props => props.theme.border};
  padding: 1rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const ProgressBar = styled.div`
  flex: 1;
  margin: 0 1rem;
`;

const ProgressTrack = styled.div`
  height: 4px;
  background: ${props => props.theme.backgroundSecondary};
  border-radius: 2px;
  overflow: hidden;
`;

const ProgressFill = styled.div`
  height: 100%;
  background: ${props => props.theme.primary};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const ProgressText = styled.div`
  font-size: 0.9rem;
  color: ${props => props.theme.textLight};
  text-align: center;
  margin-top: 0.5rem;
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const NavButton = styled(ActionButton)`
  width: 48px;
  height: 48px;
  font-size: 1.2rem;

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;

    &:hover {
      background: ${props => props.theme.backgroundSecondary};
      transform: none;
    }
  }
`;

const SettingsPanel = styled(motion.div)`
  position: absolute;
  bottom: 80px;
  left: 50%;
  transform: translateX(-50%);
  background: ${props => props.theme.card};
  border: 1px solid ${props => props.theme.border};
  border-radius: 12px;
  padding: 1.5rem;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  z-index: 100;
  min-width: 300px;
`;

const SettingGroup = styled.div`
  margin-bottom: 1.5rem;

  &:last-child {
    margin-bottom: 0;
  }
`;

const SettingLabel = styled.label`
  display: block;
  font-size: 0.9rem;
  font-weight: 500;
  color: ${props => props.theme.text};
  margin-bottom: 0.5rem;
`;

const SettingSlider = styled.input`
  width: 100%;
  -webkit-appearance: none;
  height: 6px;
  border-radius: 3px;
  background: ${props => props.theme.backgroundSecondary};
  outline: none;

  &::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: ${props => props.theme.primary};
    cursor: pointer;
  }
`;

const SettingValue = styled.span`
  font-size: 0.8rem;
  color: ${props => props.theme.textLight};
  margin-left: 0.5rem;
`;

const BookReader = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, updateUser } = useContext(UserContext);
  const contentRef = useRef(null);

  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [bookContent, setBookContent] = useState('');
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [fontSize, setFontSize] = useState(1.1);
  const [lineHeight, setLineHeight] = useState(1.6);
  const [readingProgress, setReadingProgress] = useState(0);

  // Fetch book details
  const { data: bookData, isLoading: bookLoading } = useQuery(
    ['book', id],
    () => apiService.getBook(id),
    {
      enabled: !!id,
      staleTime: 5 * 60 * 1000,
    }
  );

  // Fetch book content
  const { data: contentData, isLoading: contentLoading } = useQuery(
    ['book-content', id],
    () => apiService.getBookContent(id),
    {
      enabled: !!id,
      staleTime: 10 * 60 * 1000,
    }
  );

  useEffect(() => {
    if (contentData?.data?.content) {
      setBookContent(contentData.data.content);
      // Split content into pages (simplified)
      const words = contentData.data.content.split(' ');
      setTotalPages(Math.ceil(words.length / 300)); // ~300 words per page
    }
  }, [contentData]);

  useEffect(() => {
    if (bookData?.data) {
      const book = bookData.data;
      setIsFavorite(user?.favorites?.includes(book.id) || false);
      // Load reading progress
      const savedProgress = localStorage.getItem(`book-progress-${book.id}`);
      if (savedProgress) {
        setCurrentPage(parseInt(savedProgress));
      }
    }
  }, [bookData, user]);

  useEffect(() => {
    // Update progress
    setReadingProgress((currentPage / totalPages) * 100);

    // Save progress
    if (bookData?.data) {
      localStorage.setItem(`book-progress-${bookData.data.id}`, currentPage.toString());
    }
  }, [currentPage, totalPages, bookData]);

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(prev => prev - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(prev => prev + 1);
    }
  };

  const handleBookmark = () => {
    setIsBookmarked(!isBookmarked);
    toast.success(isBookmarked ? '–ó–∞–∫–ª–∞–¥–∫–∞ —É–¥–∞–ª–µ–Ω–∞' : '–ó–∞–∫–ª–∞–¥–∫–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∞');
  };

  const handleFavorite = async () => {
    if (!user) {
      toast.error('–ù–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
      return;
    }

    const newFavorites = isFavorite
      ? (user.favorites || []).filter(bookId => bookId !== id)
      : [...(user.favorites || []), id];

    const updatedUser = {
      ...user,
      favorites: newFavorites
    };

    updateUser(updatedUser);
    setIsFavorite(!isFavorite);
    toast.success(isFavorite ? '–£–¥–∞–ª–µ–Ω–æ –∏–∑ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ' : '–î–æ–±–∞–≤–ª–µ–Ω–æ –≤ –∏–∑–±—Ä–∞–Ω–Ω–æ–µ');
  };

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: bookData?.data?.title,
          text: `–ß–∏—Ç–∞—é "${bookData?.data?.title}" –∞–≤—Ç–æ—Ä–∞ ${bookData?.data?.author}`,
          url: window.location.href,
        });
      } catch (error) {
        console.log('Error sharing:', error);
      }
    } else {
      // Fallback: copy to clipboard
      navigator.clipboard.writeText(window.location.href);
      toast.success('–°—Å—ã–ª–∫–∞ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∞');
    }
  };

  const handleFinishBook = async () => {
    if (!user) return;

    try {
      // Update reading stats
      const updatedUser = {
        ...user,
        stats: {
          ...user.stats,
          totalRead: (user.stats.totalRead || 0) + 1,
          totalPagesRead: (user.stats.totalPagesRead || 0) + bookData?.data?.pages,
        }
      };

      updateUser(updatedUser);

      // Show achievement notification
      toast.success('üéâ –ö–Ω–∏–≥–∞ –ø—Ä–æ—á–∏—Ç–∞–Ω–∞! –ü–æ–ª—É—á–µ–Ω –æ–ø—ã—Ç –∏ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è!');

      // Navigate back
      navigate('/catalog');
    } catch (error) {
      console.error('Error finishing book:', error);
    }
  };

  if (bookLoading || contentLoading) {
    return <LoadingSpinner text="–ó–∞–≥—Ä—É–∑–∫–∞ –∫–Ω–∏–≥–∏..." />;
  }

  if (!bookData?.data) {
    return (
      <ReaderContainer>
        <div style={{ textAlign: 'center', padding: '4rem' }}>
          <h2>–ö–Ω–∏–≥–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞</h2>
          <button onClick={() => navigate('/catalog')}>–í–µ—Ä–Ω—É—Ç—å—Å—è –∫ –∫–∞—Ç–∞–ª–æ–≥—É</button>
        </div>
      </ReaderContainer>
    );
  }

  const book = bookData.data;

  return (
    <ReaderContainer>
      <ReaderHeader>
        <ActionButton onClick={() => navigate('/catalog')}>
          <FiChevronLeft />
        </ActionButton>

        <BookInfo>
          <BookTitle>{book.title}</BookTitle>
          <BookAuthor>{book.author}</BookAuthor>
        </BookInfo>

        <ReaderActions>
          <ActionButton onClick={handleFavorite}>
            {isFavorite ? <FaHeart color="#E53E3E" /> : <FiHeart />}
          </ActionButton>
          <ActionButton onClick={handleBookmark}>
            {isBookmarked ? <FaBookmark color="#3182CE" /> : <FiBookmark />}
          </ActionButton>
          <ActionButton onClick={handleShare}>
            <FiShare2 />
          </ActionButton>
          <ActionButton onClick={() => setShowSettings(!showSettings)}>
            <FiSettings />
          </ActionButton>
        </ReaderActions>
      </ReaderHeader>

      <ReaderContent>
        <TextContent
          ref={contentRef}
          fontSize={`${fontSize}rem`}
          lineHeight={lineHeight}
          dangerouslySetInnerHTML={{
            __html: bookContent ? bookContent.replace(/\n/g, '<br/>') : '–°–æ–¥–µ—Ä–∂–∏–º–æ–µ –∫–Ω–∏–≥–∏ –∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è...'
          }}
        />

        <AnimatePresence>
          {showSettings && (
            <SettingsPanel
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 20 }}
              transition={{ duration: 0.2 }}
            >
              <SettingGroup>
                <SettingLabel>
                  –†–∞–∑–º–µ—Ä —à—Ä–∏—Ñ—Ç–∞: {fontSize}rem
                </SettingLabel>
                <SettingSlider
                  type="range"
                  min="0.8"
                  max="2.0"
                  step="0.1"
                  value={fontSize}
                  onChange={(e) => setFontSize(parseFloat(e.target.value))}
                />
              </SettingGroup>

              <SettingGroup>
                <SettingLabel>
                  –ú–µ–∂—Å—Ç—Ä–æ—á–Ω—ã–π –∏–Ω—Ç–µ—Ä–≤–∞–ª: {lineHeight}
                </SettingLabel>
                <SettingSlider
                  type="range"
                  min="1.2"
                  max="2.0"
                  step="0.1"
                  value={lineHeight}
                  onChange={(e) => setLineHeight(parseFloat(e.target.value))}
                />
              </SettingGroup>
            </SettingsPanel>
          )}
        </AnimatePresence>
      </ReaderContent>

      <ReaderFooter>
        <NavigationButtons>
          <NavButton onClick={handlePreviousPage} disabled={currentPage <= 1}>
            <FiChevronLeft />
          </NavButton>
        </NavigationButtons>

        <ProgressBar>
          <ProgressTrack>
            <ProgressFill style={{ width: `${readingProgress}%` }} />
          </ProgressTrack>
          <ProgressText>
            {currentPage} / {totalPages} —Å—Ç—Ä–∞–Ω–∏—Ü ({Math.round(readingProgress)}%)
          </ProgressText>
        </ProgressBar>

        <NavigationButtons>
          <NavButton onClick={handleNextPage} disabled={currentPage >= totalPages}>
            <FiChevronRight />
          </NavButton>
        </NavigationButtons>
      </ReaderFooter>

      {currentPage === totalPages && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          style={{
            position: 'fixed',
            bottom: '120px',
            left: '50%',
            transform: 'translateX(-50%)',
            background: '#4CAF50',
            color: 'white',
            padding: '1rem 2rem',
            borderRadius: '8px',
            cursor: 'pointer',
            zIndex: 1000,
          }}
          onClick={handleFinishBook}
        >
          üéâ –ó–∞–≤–µ—Ä—à–∏—Ç—å –∫–Ω–∏–≥—É
        </motion.div>
      )}
    </ReaderContainer>
  );
};

export default BookReader;
