import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { UserContext } from '../../context/UserContext';
import { FiSettings, FiBook, FiStar, FiTrendingUp, FiCalendar, FiHeart, FiMessageSquare, FiAward } from 'react-icons/fi';
import { FaCrown, FaMedal, FaTrophy } from 'react-icons/fa';
import BookCard from '../../components/BookCard/BookCard';

const ProfileContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const ProfileHeader = styled.div`
  display: flex;
  gap: 2rem;
  margin-bottom: 3rem;
  align-items: center;
  flex-wrap: wrap;
`;

const AvatarSection = styled.div`
  position: relative;
`;

const Avatar = styled.div`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  background: ${props => props.theme.primary};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
  color: white;
  border: 4px solid ${props => props.theme.border};
  position: relative;
`;

const AvatarImage = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
`;

const LevelBadge = styled.div`
  position: absolute;
  bottom: -5px;
  right: -5px;
  background: ${props => props.theme.accent};
  color: white;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.9rem;
  font-weight: bold;
  border: 3px solid ${props => props.theme.card};
`;

const ProfileInfo = styled.div`
  flex: 1;
  min-width: 300px;
  position: relative;
`;

const SettingsButton = styled(Link)`
  position: absolute;
  top: 0;
  right: 0;
  background: ${props => props.theme.backgroundSecondary};
  border: 1px solid ${props => props.theme.border};
  border-radius: 8px;
  padding: 0.5rem;
  color: ${props => props.theme.text};
  text-decoration: none;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${props => props.theme.accent};
    transform: scale(1.05);
  }
`;

const ProfileName = styled.h1`
  color: ${props => props.theme.text};
  font-size: 2.5rem;
  margin-bottom: 0.5rem;
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const ProfileRole = styled.div`
  color: ${props => props.theme.secondary};
  font-size: 1.1rem;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const ProfileStats = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
`;

const StatCard = styled.div`
  background: ${props => props.theme.card};
  border-radius: 12px;
  padding: 1.5rem;
  border: 1px solid ${props => props.theme.border};
  text-align: center;
`;

const StatIcon = styled.div`
  font-size: 2rem;
  margin-bottom: 0.5rem;
  color: ${props => props.theme.primary};
`;

const StatValue = styled.div`
  font-size: 1.8rem;
  font-weight: bold;
  color: ${props => props.theme.text};
  margin-bottom: 0.25rem;
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  color: ${props => props.theme.textLight};
`;

const LevelSection = styled.div`
  background: ${props => props.theme.card};
  border-radius: 12px;
  padding: 2rem;
  margin-bottom: 2rem;
  border: 1px solid ${props => props.theme.border};
`;

const LevelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const LevelTitle = styled.h3`
  color: ${props => props.theme.text};
  font-size: 1.2rem;
  margin: 0;
`;

const LevelValue = styled.span`
  color: ${props => props.theme.primary};
  font-weight: bold;
  font-size: 1.1rem;
`;

const ProgressBar = styled.div`
  height: 8px;
  background: ${props => props.theme.backgroundSecondary};
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 0.5rem;
`;

const ProgressFill = styled.div`
  height: 100%;
  background: linear-gradient(90deg, ${props => props.theme.primary}, ${props => props.theme.accent});
  border-radius: 4px;
  transition: width 0.3s ease;
`;

const ProgressText = styled.div`
  font-size: 0.9rem;
  color: ${props => props.theme.textLight};
  text-align: right;
`;

const TabsContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  border-bottom: 1px solid ${props => props.theme.border};
  padding-bottom: 1rem;
  overflow-x: auto;
`;

const Tab = styled.button`
  padding: 0.75rem 1.5rem;
  border: none;
  background: ${props => props.active ? props.theme.primary : 'transparent'};
  color: ${props => props.active ? 'white' : props.theme.text};
  border-radius: 8px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;

  &:hover {
    background: ${props => props.active ? props.theme.primaryDark : props.theme.accent};
  }
`;

const ContentSection = styled.div`
  background: ${props => props.theme.card};
  border-radius: 12px;
  padding: 2rem;
  border: 1px solid ${props => props.theme.border};
`;

const SectionTitle = styled.h3`
  color: ${props => props.theme.text};
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const BooksGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1.5rem;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem;
  color: ${props => props.theme.textLight};
`;

const EmptyIcon = styled.div`
  font-size: 3rem;
  margin-bottom: 1rem;
  opacity: 0.5;
`;

const Profile = () => {
  const { user } = useContext(UserContext);
  const [activeTab, setActiveTab] = useState('overview');

  const tabs = [
    { id: 'overview', label: '–û–±–∑–æ—Ä', icon: 'üìä' },
    { id: 'books', label: '–ú–æ–∏ –∫–Ω–∏–≥–∏', icon: 'üìö' },
    { id: 'favorites', label: '–ò–∑–±—Ä–∞–Ω–Ω–æ–µ', icon: '‚ù§Ô∏è' },
    { id: 'reviews', label: '–û—Ç–∑—ã–≤—ã', icon: 'üí¨' },
    { id: 'achievements', label: '–î–æ—Å—Ç–∏–∂–µ–Ω–∏—è', icon: 'üèÜ' },
    { id: 'history', label: '–ò—Å—Ç–æ—Ä–∏—è', icon: 'üìñ' },
  ];

  const getRoleIcon = (role) => {
    switch(role) {
      case '–í–ª–∞–¥–µ–ª–µ—Ü': return <FaCrown color="#FFD700" />;
      case '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä': return <FaTrophy color="#FF6B6B" />;
      case '–ú–æ–¥–µ—Ä–∞—Ç–æ—Ä': return <FaMedal color="#4ECDC4" />;
      case 'VIP': return <FaStar color="#9B59B6" />;
      default: return null;
    }
  };

  const renderOverview = () => (
    <div>
      <ProfileStats>
        <StatCard>
          <StatIcon><FiBook /></StatIcon>
          <StatValue>{user?.stats?.totalRead || 0}</StatValue>
          <StatLabel>–ü—Ä–æ—á–∏—Ç–∞–Ω–æ –∫–Ω–∏–≥</StatLabel>
        </StatCard>
        <StatCard>
          <StatIcon><FiStar /></StatIcon>
          <StatValue>{user?.stats?.reviewsWritten || 0}</StatValue>
          <StatLabel>–ù–∞–ø–∏—Å–∞–Ω–æ –æ—Ç–∑—ã–≤–æ–≤</StatLabel>
        </StatCard>
        <StatCard>
          <StatIcon><FiTrendingUp /></StatIcon>
          <StatValue>{user?.stats?.readingDays || 0}</StatValue>
          <StatLabel>–î–Ω–µ–π —Å –Ω–∞–º–∏</StatLabel>
        </StatCard>
        <StatCard>
          <StatIcon><FiAward /></StatIcon>
          <StatValue>{user?.achievements?.length || 0}</StatValue>
          <StatLabel>–î–æ—Å—Ç–∏–∂–µ–Ω–∏–π</StatLabel>
        </StatCard>
