    border: 1px solid #C6F6D5;
  ` : `
    background: #FFF5F5;
    color: #742A2A;
    border: 1px solid #FEB2B2;
  `}
`;

const BookCard = ({ book }) => {
  const navigate = useNavigate();
  const { user, updateUser } = useContext(UserContext);
  const [isFavorite, setIsFavorite] = useState(
    user?.favorites?.includes(book.id) || false
  );

  const handleCardClick = () => {
    navigate(`/book/${book.id}`);
  };

  const handleReadOnline = (e) => {
    e.stopPropagation();
    navigate(`/book/${book.id}`);
  };

  const handleBorrow = (e) => {
    e.stopPropagation();

    if (!user) {
      alert('–ù–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
      return;
    }

    // Simple mock borrowing
    const updatedUser = {
      ...user,
      borrowedBooks: [...(user.borrowedBooks || []), book.id]
    };
    updateUser(updatedUser);

    alert(`–ö–Ω–∏–≥–∞ "${book.title}" –¥–æ–±–∞–≤–ª–µ–Ω–∞ –≤ –≤–∞—à–∏ –∫–Ω–∏–≥–∏! –ß–∏—Ç–∞—Ç—å –æ–Ω–ª–∞–π–Ω –º–æ–∂–Ω–æ –≤ –ª—é–±–æ–µ –≤—Ä–µ–º—è.`);
  };

  const handleDownload = (e) => {
    e.stopPropagation();
    alert(`–°–∫–∞—á–∏–≤–∞–Ω–∏–µ –∫–Ω–∏–≥–∏ "${book.title}" –ø–æ–∫–∞ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–æ. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ —Ñ—É–Ω–∫—Ü–∏—é "–ß–∏—Ç–∞—Ç—å –æ–Ω–ª–∞–π–Ω".`);
  };

  const handleFavorite = (e) => {
    e.stopPropagation();

    if (!user) {
      alert('–ù–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
      return;
    }

    const newFavorites = isFavorite
      ? (user.favorites || []).filter(id => id !== book.id)
      : [...(user.favorites || []), book.id];

    const updatedUser = {
      ...user,
      favorites: newFavorites
    };

    updateUser(updatedUser);
    setIsFavorite(!isFavorite);

    alert(isFavorite ? '–£–¥–∞–ª–µ–Ω–æ –∏–∑ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ' : '–î–æ–±–∞–≤–ª–µ–Ω–æ –≤ –∏–∑–±—Ä–∞–Ω–Ω–æ–µ');
  };

  const handleShare = (e) => {
    e.stopPropagation();

    const url = `${window.location.origin}/book/${book.id}`;
    navigator.clipboard.writeText(url);
    alert('–°—Å—ã–ª–∫–∞ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∞ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞!');
  };

  const handleComments = (e) => {
    e.stopPropagation();
    navigate('/reviews');
  };

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < 5; i++) {
      if (i < fullStars) {
        stars.push(<Star key={i} filled />);
      } else if (i === fullStars && hasHalfStar) {
        stars.push(<Star key={i} filled />);
      } else {
        stars.push(<Star key={i} />);
      }
    }

    return stars;
  };

  return (
    <Card
      onClick={handleCardClick}
      whileHover={{ y: -8 }}
      transition={{ type: "spring", stiffness: 300, damping: 20 }}
    >
      <StatusBadge available={book.available}>
        {book.available ? '–î–æ—Å—Ç—É–ø–Ω–æ' : '–ó–∞–Ω—è—Ç–æ'}
      </StatusBadge>

      <FavoriteButton
        isFavorite={isFavorite}
        onClick={handleFavorite}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        {isFavorite ? <FaHeart /> : <FiHeart />}
      </FavoriteButton>

      <BookHeader>
        <BookCover>
          <BookIcon>
            {book.icon || 'üìñ'}
          </BookIcon>
        </BookCover>

        <BookInfo>
          <BookTitle>{book.title}</BookTitle>
          <BookAuthor>{book.author}</BookAuthor>

          <BookMeta>
            <MetaItem>{book.year}</MetaItem>
            <MetaItem>{book.genre}</MetaItem>
            <MetaItem>{book.pages} —Å—Ç—Ä.</MetaItem>
          </BookMeta>

          <BookRating>
            <Stars>
              {renderStars(book.rating || 0)}
            </Stars>
            <RatingValue>{book.rating || 0}</RatingValue>
            <ReviewsCount>({book.reviewsCount || 0})</ReviewsCount>
          </BookRating>
        </BookInfo>
      </BookHeader>

      <BookActions>
        <ActionButton
          primary
          onClick={handleReadOnline}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <FiBookOpen />
          –ß–∏—Ç–∞—Ç—å –æ–Ω–ª–∞–π–Ω
        </ActionButton>
        <ActionButton
          onClick={handleBorrow}
          disabled={!book.available}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          üìö
          {book.available ? '–í–∑—è—Ç—å –∫–Ω–∏–≥—É' : '–ó–∞–Ω—è—Ç–æ'}
        </ActionButton>
        <ActionButton
          onClick={handleDownload}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          ‚¨áÔ∏è
          –°–∫–∞—á–∞—Ç—å
        </ActionButton>
      </BookActions>

      <SocialActions>
        <SocialButton
          onClick={handleShare}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <FiShare2 size={14} />
          –ü–æ–¥–µ–ª–∏—Ç—å—Å—è
        </SocialButton>
        <SocialButton
          onClick={handleComments}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <FiMessageSquare size={14} />
          –û—Ç–∑—ã–≤—ã ({book.reviewsCount || 0})
        </SocialButton>
      </SocialActions>
    </Card>
  );
};

export default BookCard;
